import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';
import {
  Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button, Modal, ModalHeader
} from 'reactstrap';
import { Link } from "react-router-dom";

const Record = (props) => (

  <tr>
    <td>{props.record.courseTitle}</td>
    <td>{props.record.termDesc}</td>
    <td>{props.record.courseNumber}</td>
    <td>{props.record.subjectDescription}</td>
    <Button onClick="">Add</Button>
    <td>
    </td>
  </tr>
);

export default class RecordList extends Component {


  // This is the constructor that shall store our data retrieved from the database
  constructor(props) {
    super(props);
    this.deleteRecord = this.deleteRecord.bind(this);
    this.state = { records: [], students: []};
  }

  // This method will get the data from the database.
  componentDidMount() {

    let one = "http://localhost:5000/record/"
    let two = "http://localhost:5000/student/"

    const requestOne = axios.get(one);
    const requestTwo = axios.get(two);

    axios.all([requestOne, requestTwo]).then(axios.spread((...responses) => {
      this.setState({ records: responses[0].data, students: responses[1].data })
      })).catch(function (error) {
        console.log(error);
      })
  }

  // This method will delete a record based on the method
  deleteRecord(id) {
    axios.delete("http://localhost:5000/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      record: this.state.records.filter((el) => el._id !== id),
    });
  }

  // This method will map out the users on the table
  recordList() {
    if(!this.state.records.taken){
      document.getElementById('row').classList.add('table-success');
    }
    return this.state.records.map((currentrecord) => {
      return (
        <Record
          record={currentrecord}
          deleteRecord={this.deleteRecord}
          key={currentrecord._id}
        />
      );
    });
  }

  recordStudents() {
    return this.state.students.map((currentrecord) => {
      return (
        <Record
          record={currentrecord}
          deleteRecord={this.deleteRecord}
          key={currentrecord._id}
        />
      );
    });
  }


  // This following section will display the table with the records of individuals.
  render() {
    return (

      <div class="container">
  <div class="row">
    <div class="col-sm">
    <div>
      <div class="container-sm width:50px">
        <h3>Course Catalog</h3>
        <table id="row" className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Course Name</th>
              <th>Term</th>
              <th>Course Number</th>
              <th>Department</th>
            </tr>
          </thead>
          <tbody>{this.recordList()}</tbody>
        </table>
      </div>
      </div>
    </div>
    <div class="col-sm">
    <div>
      <div class="container-sm width:50px">
        <h3>Current Classes</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Course Name</th>
              <th>Term</th>
              <th>Course Number</th>
              <th>Department</th>
            </tr>
          </thead>
          <tbody>{this.recordStudents()}</tbody>
        </table>
      </div>
      </div>
    </div>
    </div>
    <div class="col-sm">
      One of three columns
    </div>
  </div>

    );
  }
}
